pragma solidity >=0.4.22 <0.9.0;

contract SocialMedia{

	uint public postCount = 0;

	struct Comment{
		uint id;
		string comment;
	}

	struct Post{
		uint id;
		string content;
		uint commentNum;
	}

	event PostCreated(
		uint id,
		string content,
		uint commentNum

	);

	event CommentPosted(
		uint id,
		string comment
	);

	mapping (uint => Post) public posts;
	mapping (uint => Comment[]) public comments;

	function createPost(string memory _content) public
	{
		postCount++;
		posts[postCount] = Post(postCount, _content, 0);
		emit PostCreated(postCount, _content, 0);
	}

	function createComment(uint _id, string memory _comment) public
	{
		Comment[] memory _com = comments[_id];
		Post memory _post = posts[_id];
		_post.commentNum++;
		posts[_id] = _post;
		_com[_post.commentNum] = Comment(_post.commentNum, _comment);
		emit CommentPosted(_id, _comment);
	}

}
